# Ð—Ð±ÐµÑ€Ñ–Ð³Ð°Ñ”Ð¼Ð¾ Ð¿Ð¾Ñ‚Ð¾Ñ‡Ð½Ñ– Ð·Ð°Ð²Ð´Ð°Ð½Ð½Ñ Ð´Ð»Ñ ÐºÐ¾Ð¶Ð½Ð¾Ð³Ð¾ ÐºÐ¾Ñ€Ð¸ÑÑ‚ÑƒÐ²Ð°Ñ‡Ð°
user_tasks = {}

def generate_tasks():
    return random.sample(ALL_TASKS, 6)

async def start(update: Update, context: ContextTypes.DEFAULT_TYPE) -> None:
    user_id = update.effective_user.id
    user_tasks[user_id] = generate_tasks()
    await show_tasks(update, context)

async def show_tasks(update: Update, context: ContextTypes.DEFAULT_TYPE) -> None:
    user_id = update.effective_user.id
    tasks = user_tasks.get(user_id, generate_tasks())
    
    keyboard = []
    for i, task in enumerate(tasks):
        keyboard.append([InlineKeyboardButton(f"â™»ï¸ Ð—Ð°Ð¼Ñ–Ð½Ð¸Ñ‚Ð¸ Ð·Ð°Ð²Ð´Ð°Ð½Ð½Ñ {i+1}", callback_data=f"replace_{i}")])
    
    reply_markup = InlineKeyboardMarkup(keyboard)
    
    message_text = "ðŸ“Œ Ð’Ð°ÑˆÑ– Ð¿Ð¾Ñ‚Ð¾Ñ‡Ð½Ñ– Ð·Ð°Ð²Ð´Ð°Ð½Ð½Ñ:\n\n" + "\n".join(
        f"{i+1}. {task}" for i, task in enumerate(tasks)
    )
    
    if update.callback_query:
        await update.callback_query.edit_message_text(text=message_text, reply_markup=reply_markup)
    else:
        await update.message.reply_text(text=message_text, reply_markup=reply_markup)

async def replace_task(update: Update, context: ContextTypes.DEFAULT_TYPE) -> None:
    query = update.callback_query
    await query.answer()
    
    user_id = update.effective_user.id
    task_index = int(query.data.split("_")[1])
    
    # Ð“ÐµÐ½ÐµÑ€ÑƒÑ”Ð¼Ð¾ Ð½Ð¾Ð²Ðµ Ð·Ð°Ð²Ð´Ð°Ð½Ð½Ñ, Ð²Ñ–Ð´Ð¼Ñ–Ð½Ð½Ðµ Ð²Ñ–Ð´ Ð¿Ð¾Ñ‚Ð¾Ñ‡Ð½Ð¸Ñ…
    current_tasks = user_tasks[user_id]
    new_task = random.choice([t for t in ALL_TASKS if t not in current_tasks])
    current_tasks[task_index] = new_task
    user_tasks[user_id] = current_tasks
    
    await show_tasks(update, context)

def main() -> None:
    application = Application.builder().token("7615231270:AAHWyL3-QGY6GUYFM46D5UP-dcAEQCymlEw").build()
    
    application.add_handler(CommandHandler("start", start))
    application.add_handler(CallbackQueryHandler(replace_task, pattern="^replace_"))
    
    application.run_polling()

if __name__ == "__main__":
    main()



    
