# –°–ª–æ–≤–Ω–∏–∫–∏ –¥–ª—è –∑–±–µ—Ä—ñ–≥–∞–Ω–Ω—è —Å—Ç–∞–Ω—É
user_tasks = {}
user_task_status = {}

def generate_tasks():
    """–ì–µ–Ω–µ—Ä—É—î —Å–ø–∏—Å–æ–∫ –∑ 6 —É–Ω—ñ–∫–∞–ª—å–Ω–∏—Ö –∑–∞–≤–¥–∞–Ω—å"""
    return random.sample(ALL_TASKS, 6)

async def start(update: Update, context: ContextTypes.DEFAULT_TYPE) -> None:
    """–û–±—Ä–æ–±–Ω–∏–∫ –∫–æ–º–∞–Ω–¥–∏ /start"""
    user_id = update.effective_user.id
    user_tasks[user_id] = generate_tasks()
    user_task_status[user_id] = ["‚ûñ"] * 6
    await show_tasks(update, context)

async def show_tasks(update: Update, context: ContextTypes.DEFAULT_TYPE) -> None:
    """–í—ñ–¥–æ–±—Ä–∞–∂–∞—î —Å–ø–∏—Å–æ–∫ –∑–∞–≤–¥–∞–Ω—å –∑ –∫–Ω–æ–ø–∫–∞–º–∏"""
    user_id = update.effective_user.id
    tasks = user_tasks.get(user_id, generate_tasks())
    statuses = user_task_status.get(user_id, ["‚ûñ"] * 6)
    
    # –§–æ—Ä–º—É–≤–∞–Ω–Ω—è –∫–ª–∞–≤—ñ–∞—Ç—É—Ä–∏
    keyboard = [
        [
            InlineKeyboardButton("‚úÖ", callback_data=f"done_{i}"),
            InlineKeyboardButton("‚ùå", callback_data=f"delete_{i}"),
            InlineKeyboardButton("‚ôªÔ∏è", callback_data=f"replace_{i}")
        ]
        for i in range(len(tasks))
    ]
    
    # –§–æ—Ä–º—É–≤–∞–Ω–Ω—è —Ç–µ–∫—Å—Ç—É –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è
    message_lines = [
        f"{i+1}. {status} {task}"
        for i, (task, status) in enumerate(zip(tasks, statuses))
    ]
    message_text = "üìå –í–∞—à—ñ –ø–æ—Ç–æ—á–Ω—ñ –∑–∞–≤–¥–∞–Ω–Ω—è:\n\n" + "\n".join(message_lines)
    
    # –í—ñ–¥–ø—Ä–∞–≤–∫–∞ –∞–±–æ –æ–Ω–æ–≤–ª–µ–Ω–Ω—è –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è
    if update.callback_query:
        await update.callback_query.edit_message_text(
            text=message_text,
            reply_markup=InlineKeyboardMarkup(keyboard)
        )
    else:
        await update.message.reply_text(
            text=message_text,
            reply_markup=InlineKeyboardMarkup(keyboard)
        )

async def button_handler(update: Update, context: ContextTypes.DEFAULT_TYPE) -> None:
    """–û–±—Ä–æ–±–Ω–∏–∫ –Ω–∞—Ç–∏—Å–∫–∞–Ω—å –∫–Ω–æ–ø–æ–∫"""
    query = update.callback_query
    await query.answer()
    
    user_id = update.effective_user.id
    action, task_index = query.data.split("_")
    task_index = int(task_index)
    
    # –Ü–Ω—ñ—Ü—ñ–∞–ª—ñ–∑–∞—Ü—ñ—è –¥–∞–Ω–∏—Ö, —è–∫—â–æ –ø–æ—Ç—Ä—ñ–±–Ω–æ
    if user_id not in user_tasks:
        user_tasks[user_id] = generate_tasks()
    if user_id not in user_task_status:
        user_task_status[user_id] = ["‚ûñ"] * 6
    
    # –û–±—Ä–æ–±–∫–∞ —Ä—ñ–∑–Ω–∏—Ö –¥—ñ–π
    if action == "replace":
        available_tasks = [t for t in ALL_TASKS if t not in user_tasks[user_id]]
        if available_tasks:
            user_tasks[user_id][task_index] = random.choice(available_tasks)
            user_task_status[user_id][task_index] = "‚ûñ"
    
    elif action == "done":
        user_task_status[user_id][task_index] = "‚úÖ"
    
    elif action == "delete":
        user_tasks[user_id][task_index] = "---"
        user_task_status[user_id][task_index] = "‚ùå"
    
    await show_tasks(update, context)

def main() -> None:
    """–¢–æ—á–∫–∞ –≤—Ö–æ–¥—É –ø—Ä–æ–≥—Ä–∞–º–∏"""
    application = Application.builder().token("–í–ê–®_–¢–û–ö–ï–ù").build()
    
    # –î–æ–¥–∞–≤–∞–Ω–Ω—è –æ–±—Ä–æ–±–Ω–∏–∫—ñ–≤
    application.add_handler(CommandHandler("start", start))
    application.add_handler(CallbackQueryHandler(button_handler, pattern=r"^(done|delete|replace)_\d+$"))
    
    # –ó–∞–ø—É—Å–∫ –±–æ—Ç–∞
    application.run_polling()

if __name__ == "__main__":
    main()
